def inplace(f):
    def _f(b):
        f(b)
        return b
    return _f

# %% ../nbs/Fashion_mnist.ipynb 13
def collate_dict(ds):
    get = itemgetter(*ds.features)
    def _f(b): return get(default_collate(b))
    return _f

